import React from 'react';
import { render, waitFor, fireEvent } from '@testing-library/react';
import axios from 'axios';
import Panier from '../src/components/Panier';
import { act } from 'react-dom/test-utils';

jest.mock('axios');

describe('Panier component', () => {
  it('renders without crashing', () => {
    render(<Panier />);
  });

  it('fetches cart data correctly', async () => {
    const data = [{ id: 1, name: 'Product 1', price: 100, image: 'image1.jpg' }];
    axios.get.mockResolvedValueOnce({ data });

    let container;
    await act(async () => {
      container = render(<Panier />);
    });

    await waitFor(() => expect(axios.get).toHaveBeenCalledTimes(1));
    expect(container.getByText('Product 1')).toBeInTheDocument();
    expect(container.getByText('100 €')).toBeInTheDocument();
  });

  it('deletes product correctly', async () => {
    const data = [{ id: 1, name: 'Product 1', price: 100, image: 'image1.jpg' }];
    axios.get.mockResolvedValueOnce({ data });
    axios.delete.mockResolvedValueOnce();

    let container;
    await act(async () => {
      container = render(<Panier />);
    });

    fireEvent.click(container.getByText('Supprimer'));
    await waitFor(() => expect(axios.delete).toHaveBeenCalledTimes(1));
    expect(container.queryByText('Product 1')).toBeNull();
  });
});
