import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { jwtDecode} from 'jwt-decode';
import Cookies from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.min.css';

function Panier() {
    const [produits, setProduits] = useState([]);

    useEffect(() => {
        fetchPanier();
    }, []);



    const fetchPanier = async () => {
        try {
            const token = Cookies.get('token');
            if (!token) {
                throw new Error('Token not found');
            }
            const decodedToken = jwtDecode(token);
            const response = await axios.get('http://localhost:3000/api/cartroute/getContenuPanier', {
    params: { uid: decodedToken.uid }
});
            setProduits(response.data);
        } catch (error) {
            if (error.response && error.response.status === 404) {
                console.error('The requested resource was not found:', error);
            } else {
                console.error('Erreur lors de la récupération du panier :', error);
            }
        }
    };

const supprimerProduit = async (pid) => {
    try {
        const token = Cookies.get('token');
        if (!token) {
            throw new Error('Token not found');
        }
        const decodedToken = jwtDecode(token);
        await axios.delete(`http://localhost:3000/api/cartroute/${decodedToken.uid}/${pid}`);
        setProduits(produits.filter((produit) => produit.id !== pid));
    } catch (error) {
        console.error('Erreur lors de la suppression du produit :', error);
    }
};



    return (
        <div className="container">
            <h1 className="my-4">Contenu du panier</h1>
            {produits.map((produit, index) => (
                <div key={index} className="card mb-3">
                    <div className="row g-0">
                        <div className="col-md-4">
                            <img src={`http://localhost:3000/${produit.image}`} alt={produit.name} className="img-fluid" />
                        </div>
                        <div className="col-md-8">
                            <div className="card-body">
                                <h5 className="card-title">{produit.name}</h5>
                                <p className="card-text">{produit.price} €</p>
                                <button className="btn btn-danger" onClick={() => supprimerProduit(produit.id)}>Supprimer</button>
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Panier;
